eif(!ctype_digit($input_amount)){                                    $amount_err = "Please enter a positive integer value.";                                } else{                                    $amount = $input_amount;                                }                                                            if(empty($amount_err)){                                // Prepare an insert statement                                $sql = "SELECT stockname FROM stockideal where capital == 'LARGE'";                                                                 if($stmt = mysqli_prepare($link, $sql)){                                    // Bind variables to the prepared statement as parameters                                    mysqli_stmt_bind_param($stmt, "i", $amount);                                                                        // Attempt to execute the prepared statement                                    if(mysqli_stmt_execute($stmt)){                                        $result = mysqli_stmt_get_result($stmt);                                            if(mysqli_num_rows($result) == 1){                                            /* Fetch result row as an associative array. Since the result set                                            contains only one row, we don't need to use while loop */                                            $row = mysqli_fetch_array($result, MYSQLI_ASSOC);                                                                                        // Retrieve individual field value                                            $stockname = $row["stockname"];                                                                                    }                                        else{                                            // URL doesn't contain valid id parameter. Redirect to error page                                            header("location: error.php");                                      